import React, { Component } from 'react';
import { Table, Button, ButtonToolbar } from 'react-bootstrap';
import { EditUserModal } from './EditUserModal';
import axios from "axios";

export class Dashboard extends Component {

    constructor(props) {
        super(props);
        this.state = { user: [] , editModalShow: false}
    }

    componentDidMount() {
        this.refreshList();
    }



     refreshList(){
        try{
            var token = localStorage.getItem('user_token');
            const res =  axios.get('http://127.0.0.1:4000/api/users',{
                headers: {
                    'authorization': `Bearer ${token}`,
                    'Accept': 'application/json',
                    'Content-Type': 'application/json',
                }
            });
            console.log(res)
            if(res){
                // this.props.history.push('/dashbord');
            }
            if (res.data === "Fails"){
                alert("please enter correct user name and password")
            }
        }catch (err){
            console.log(err)
        }
    }
    // refreshList() {
    //     var token = localStorage.getItem('user_token');
    //     fetch('http://localhost:4000/users',{
    //         method: 'GET',
    //         headers: {
    //             'Accept': 'application/json',
    //             'Content-Type': 'application/json',
    //             'token': token
    //         }
    //     })
    //
    //         .then(Response => Response.json())
    //         .then(data => {
    //             this.setState({ cate: data });
    //         })
    // }



    deleteCat(id)
    {
        if(window.confirm('Are you sure?')) {
            fetch('http://localhost:8080/category/id/'+id,{
                method: 'DELETE',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json',
                    'username': 'menuka'
                }
            })
        }
    }

    render() {
        const { user, id, name,status,version } = this.state;
        let EditModelClose = () => this.setState({ editModalShow: false })
        return (
            <div className="mt-4">
                <ButtonToolbar>

                    <EditUserModal
                        show={this.state.editModalShow}
                        onHide={EditModelClose}
                        id={id}
                        name={name}
                        status={status}
                        version={version}
                    />
                </ButtonToolbar>
                <Table className="mt-lg-5" striped bordered hover size="sm">
                    <thead>
                    <tr>
                        <th>ID</th>
                        <th>First Name</th>
                        <th>Last Name</th>
                        <th>Gender</th>
                        <th>Email</th>
                        <th>Contact Number</th>
                        <th>Option</th>
                    </tr>
                    </thead>
                    <tbody>
                    {user.map(category =>
                        <tr key={category.id}>
                            <td>{category.id}</td>
                            <td>{category.name}</td>
                            <td>{category.status}</td>
                            <td>{category.modifiedUser}</td>
                            <td>{category.modifiedDate}</td>
                            <td>{category.version}</td>
                            <td>
                                <ButtonToolbar>
                                    <Button className="mr-2" variant="info"
                                            onClick={()=> this.setState({editModalShow:true, id:category.id, name:category.name, status:category.status, version:category.version})}>
                                        Edit
                                    </Button>
                                    <Button className="mr-2" variant="danger"
                                            onClick={()=> this.deleteCat(category.id)}>
                                        Delete
                                    </Button>
                                </ButtonToolbar>
                            </td>
                        </tr>
                    )}
                    </tbody>
                </Table>

            </div>
        );

    }

}
