import React, { Component } from 'react';
import { Modal, Button, Row, Col, Form, FormGroup } from 'react-bootstrap';
import Snackbar from '@material-ui/core/Snackbar';
import IconButton from '@material-ui/core/IconButton';

export class EditUserModal extends Component {
    constructor(props) {
        super(props);
        this.state = { snackbaropen: false, snackbarmsg: '' };
        this.handleFormSubmit = this.handleFormSubmit.bind(this);
    }

    snackbarClose = (event) => {
        this.setState({ snackbaropen: false });
    };

    handleFormSubmit(event,props) {
        event.preventDefault();
        //alert(event.target.name.value);

        fetch('http://localhost:4000/api/users/'+this.props.id, {
            method: 'PATCH',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                id: event.target.id.value,
                name: event.target.name.value,
                status: event.target.status.value,
                version: this.props.version

            })
        })
            .then(res => res.json())
            .then((result) => {
                    //alert("Success");
                    this.setState({ snackbaropen: true, snackbarmsg: 'Success' })
                }, (error) => {
                    this.setState({ snackbaropen: true, snackbarmsg: 'Failed' })
                }

            )
    }

    render() {
        return (
            <div className="container">

                <Snackbar anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
                          open={this.state.snackbaropen}
                          autoHideDuration={6000}
                          onClose={this.snackbarClose}
                          message={<span id="message-id">{this.state.snackbarmsg}</span>}
                          action={[
                              <IconButton key="close" aria-label="Close" color="inherit" onClick={this.snackbarClose}></IconButton>
                          ]}
                />
                <Modal
                    {...this.props}
                    size="lg"
                    aria-labelledby="contained-modal-title-vcenter"

                >
                    <Modal.Header closeButton>
                        <Modal.Title id="contained-modal-title-vcenter">
                            Edit Category
                        </Modal.Title>
                    </Modal.Header>
                    <Modal.Body>

                        <Row>
                            <Col sm={6}>
                                {/*<Form onSubmit={this.handleSubmit}>*/}
                                {/*    <Form.Group controlId="id">*/}
                                {/*        <Form.Label>ID</Form.Label>*/}
                                {/*        <Form.Control type="text" name="id" required disabled defaultValue = {this.props.id}/>*/}
                                {/*    </Form.Group>*/}

                                {/*    <Form.Group controlId="name">*/}
                                {/*        <Form.Label>Name</Form.Label>*/}
                                {/*        <Form.Control type="text" name="name" required placeholder="Category Name" defaultValue = {this.props.name}/>*/}
                                {/*    </Form.Group>*/}
                                {/*    <Form.Group>*/}
                                {/*        <Form.Label>Status</Form.Label>*/}
                                {/*        <Form.Control as="select" required name="status" defaultValue = {this.props.status}>*/}
                                {/*            <option selected disabled>{this.props.status}</option>*/}
                                {/*            <option>ACTIVE</option>*/}
                                {/*            <option>INACTIVE</option>*/}
                                {/*        </Form.Control>*/}
                                {/*    </Form.Group>*/}
                                {/*    <Form.Group controlId="version" hidden>*/}
                                {/*        <Form.Label>Version</Form.Label>*/}
                                {/*        <Form.Control type="text" name="version" required  defaultValue = {this.props.version}/>*/}
                                {/*    </Form.Group>*/}
                                {/*    <Form.Group>*/}
                                {/*        <Button variant="primary" type="submit" >*/}
                                {/*            Edit Category*/}
                                {/*        </Button>*/}
                                {/*    </Form.Group>*/}
                                {/*</Form>*/}
                                <form onSubmit={event => this.handleFormSubmit(event)}>
                                    <div className="form-group">
                                        <label>First Name</label>
                                        <input
                                            name="first_name"
                                            type="text"
                                            className="form-control"
                                            placeholder="First Name"
                                            value={this.props.firstName}
                                        />
                                    </div>

                                    <div className="form-group">
                                        <label>Last Name</label>
                                        <input
                                            name="last_name"
                                            type="text"
                                            className="form-control"
                                            placeholder="Last Name"
                                            value={this.props.lastName}

                                        />
                                    </div>

                                    <div className="form-group">
                                        <label>Gender</label>
                                        <input
                                            name="gender"
                                            type="text"
                                            className="form-control"
                                            placeholder="Gender"
                                            value={this.props.gender}

                                        />
                                    </div>

                                    <div className="form-group">
                                        <label>Email address</label>
                                        <input
                                            name="email"
                                            type="email"
                                            className="form-control"
                                            placeholder="Enter email"
                                            value={this.props.email}

                                        />
                                    </div>

                                    <div className="form-group">
                                        <label>Contact No</label>
                                        <input
                                            name="number"
                                            type="text"
                                            className="form-control"
                                            placeholder="Contact No"
                                            value={this.props.number}

                                        />
                                    </div>
                                    <div className="form-group">
                                        <label>Contact No</label>
                                        <input
                                            name="password"
                                            type="password"
                                            className="form-control"
                                            placeholder="Password"

                                        />
                                    </div>
                                    <button type="submit" className="btn btn-primary btn-block">Update</button>

                                </form>
                            </Col>
                        </Row>

                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="danger" onClick={this.props.onHide}>Close</Button>
                    </Modal.Footer>
                </Modal>
            </div>
        );
    }
}
